package ar.edu.itba.ss.models;

import java.util.List;

public class Frame {

    double time;
    List<AcceleratedParticle> particles;

    //////////////// Autogenerated /////////////////

    public List<AcceleratedParticle> getParticles() {
        return particles;
    }

    public void setParticles(List<AcceleratedParticle> particles) {
        this.particles = particles;
    }

    public Frame withParticles(List<AcceleratedParticle> particles) {
        setParticles(particles);
        return this;
    }

    public double getTime() {
        return time;
    }

    public void setTime(double time) {
        this.time = time;
    }

    public Frame withTime(double time) {
        setTime(time);
        return this;
    }

    @Override
    public String toString() {
        return "Frame{" +
            "time=" + time +
            ", particles=" + particles +
            '}';
    }
}
